SET FOREIGN_KEY_CHECKS=1;

DROP TABLE IF EXISTS TB_BACKEND_FILTER_BACKUP; DROP TABLE IF EXISTS TB_ROUTING_CRITERIA_BACKUP; DROP TABLE IF EXISTS TB_BACKEND_FILTER_MIGR2; DROP TABLE IF EXISTS TB_ROUTING_CRITERIA_MIGR; DROP TABLE IF EXISTS TB_BACKEND_FILTER_MIGR;
CREATE TEMPORARY TABLE IF NOT EXISTS TB_BACKEND_FILTER_BACKUP AS ( SELECT * FROM TB_BACKEND_FILTER );
CREATE TEMPORARY TABLE IF NOT EXISTS TB_ROUTING_CRITERIA_BACKUP AS ( SELECT * FROM TB_ROUTING_CRITERIA );
CREATE TEMPORARY TABLE IF NOT EXISTS TB_BACKEND_FILTER_MIGR AS ( SELECT @ROWNUM:=@ROWNUM + 1 AS ID_PK, BACKEND_NAME, @ROWNUM-1 AS PRIORITY, OLD_ID, OLD_PRIO, CRITERIA_OPERATOR FROM ( SELECT TB_BACKEND_FILTER.ID_PK AS OLD_ID, BACKEND_NAME, TB_BACKEND_FILTER.CRITERIA_OPERATOR,TB_BACKEND_FILTER.PRIORITY, TB_ROUTING_CRITERIA.PRIORITY AS OLD_PRIO FROM TB_BACKEND_FILTER LEFT OUTER JOIN TB_ROUTING_CRITERIA ON TB_BACKEND_FILTER.ID_PK = TB_ROUTING_CRITERIA.FK_BACKEND_FILTER WHERE TB_BACKEND_FILTER.CRITERIA_OPERATOR='OR'
UNION ALL
SELECT DISTINCT TB_BACKEND_FILTER.ID_PK AS OLD_ID, BACKEND_NAME,TB_BACKEND_FILTER.CRITERIA_OPERATOR,TB_BACKEND_FILTER.PRIORITY, 0 AS OLD_PRIO FROM TB_BACKEND_FILTER LEFT OUTER JOIN TB_ROUTING_CRITERIA ON TB_BACKEND_FILTER.ID_PK = TB_ROUTING_CRITERIA.FK_BACKEND_FILTER WHERE TB_BACKEND_FILTER.CRITERIA_OPERATOR='AND' ORDER BY PRIORITY ASC, OLD_PRIO ASC ) P INNER JOIN (SELECT @ROWNUM := 0) R );
CREATE TEMPORARY TABLE TB_BACKEND_FILTER_MIGR2 LIKE TB_BACKEND_FILTER_MIGR; INSERT INTO TB_BACKEND_FILTER_MIGR2 SELECT * FROM TB_BACKEND_FILTER_MIGR;
CREATE TEMPORARY TABLE IF NOT EXISTS TB_ROUTING_CRITERIA_MIGR AS ( SELECT ID_PK, EXPRESSION, NAME, FK_BACKEND_FILTER, PRIORITY FROM ( SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR.ID_PK AS FK_BACKEND_FILTER, 0 AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR MIGR WHERE RC.PRIORITY = MIGR.OLD_PRIO AND RC.FK_BACKEND_FILTER = MIGR.OLD_ID AND MIGR.CRITERIA_OPERATOR = 'OR' UNION ALL SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR2.ID_PK AS FK_BACKEND_FILTER, RC.PRIORITY AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR2 MIGR2 WHERE RC.FK_BACKEND_FILTER = MIGR2.OLD_ID AND MIGR2.CRITERIA_OPERATOR = 'AND' ORDER BY ID_PK) P);
SET FOREIGN_KEY_CHECKS = 0;
TRUNCATE TABLE TB_ROUTING_CRITERIA; TRUNCATE TABLE TB_BACKEND_FILTER;
ALTER TABLE TB_BACKEND_FILTER DROP COLUMN CRITERIA_OPERATOR;
INSERT INTO TB_BACKEND_FILTER SELECT ID_PK, BACKEND_NAME, PRIORITY FROM TB_BACKEND_FILTER_MIGR;
INSERT INTO TB_ROUTING_CRITERIA SELECT * FROM TB_ROUTING_CRITERIA_MIGR;
SET FOREIGN_KEY_CHECKS = 1;

CREATE GLOBAL TEMPORARY TABLE TB_BACKEND_FILTER_BACKUP ON COMMIT PRESERVE ROWS AS ( SELECT * FROM TB_BACKEND_FILTER );
CREATE GLOBAL TEMPORARY TABLE TB_ROUTING_CRITERIA_BACKUP ON COMMIT PRESERVE ROWS AS ( SELECT * FROM TB_ROUTING_CRITERIA );
CREATE GLOBAL TEMPORARY TABLE TB_BACKEND_FILTER_MIGR ON COMMIT PRESERVE ROWS AS ( SELECT ROWNUM AS ID_PK, BACKEND_NAME, ROWNUM-1 AS PRIORITY, OLD_ID, OLD_PRIO, CRITERIA_OPERATOR FROM ( SELECT TB_BACKEND_FILTER.ID_PK AS OLD_ID, BACKEND_NAME, TB_BACKEND_FILTER.CRITERIA_OPERATOR,TB_BACKEND_FILTER.PRIORITY, TB_ROUTING_CRITERIA.PRIORITY AS OLD_PRIO FROM TB_BACKEND_FILTER LEFT OUTER JOIN TB_ROUTING_CRITERIA ON TB_BACKEND_FILTER.ID_PK = TB_ROUTING_CRITERIA.FK_BACKEND_FILTER WHERE TB_BACKEND_FILTER.CRITERIA_OPERATOR='OR'
UNION ALL
SELECT DISTINCT TB_BACKEND_FILTER.ID_PK AS OLD_ID, BACKEND_NAME,TB_BACKEND_FILTER.CRITERIA_OPERATOR,TB_BACKEND_FILTER.PRIORITY, 0 AS OLD_PRIO FROM TB_BACKEND_FILTER LEFT OUTER JOIN TB_ROUTING_CRITERIA ON TB_BACKEND_FILTER.ID_PK = TB_ROUTING_CRITERIA.FK_BACKEND_FILTER WHERE TB_BACKEND_FILTER.CRITERIA_OPERATOR='AND' ORDER BY PRIORITY ASC, OLD_PRIO ASC ) );
CREATE GLOBAL TEMPORARY TABLE TB_BACKEND_FILTER_MIGR2 ON COMMIT PRESERVE ROWS AS SELECT * FROM TB_BACKEND_FILTER_MIGR;
CREATE GLOBAL TEMPORARY TABLE TB_ROUTING_CRITERIA_MIGR ON COMMIT PRESERVE ROWS AS ( SELECT ID_PK, EXPRESSION, NAME, FK_BACKEND_FILTER, PRIORITY FROM ( SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR.ID_PK AS FK_BACKEND_FILTER, 0 AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR MIGR WHERE RC.PRIORITY = MIGR.OLD_PRIO AND RC.FK_BACKEND_FILTER = MIGR.OLD_ID AND MIGR.CRITERIA_OPERATOR = 'OR' UNION ALL SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR2.ID_PK AS FK_BACKEND_FILTER, RC.PRIORITY AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR2 MIGR2 WHERE RC.FK_BACKEND_FILTER = MIGR2.OLD_ID AND MIGR2.CRITERIA_OPERATOR = 'AND' ORDER BY ID_PK) P);
SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR.ID_PK AS FK_BACKEND_FILTER, 0 AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR MIGR WHERE RC.PRIORITY = MIGR.OLD_PRIO AND RC.FK_BACKEND_FILTER = MIGR.OLD_ID AND MIGR.CRITERIA_OPERATOR = 'OR' UNION ALL SELECT RC.ID_PK AS ID_PK, RC.EXPRESSION AS EXPRESSION, RC.NAME AS NAME, MIGR2.ID_PK AS FK_BACKEND_FILTER, RC.PRIORITY AS PRIORITY FROM TB_ROUTING_CRITERIA RC, TB_BACKEND_FILTER_MIGR2 MIGR2 WHERE RC.FK_BACKEND_FILTER = MIGR2.OLD_ID AND MIGR2.CRITERIA_OPERATOR = 'AND' ORDER BY ID_PK;
ALTER TABLE TB_ROUTING_CRITERIA DISABLE CONSTRAINT FK_I7GEI6BDR2CDN61HDOPLXBU7P; TRUNCATE TABLE TB_ROUTING_CRITERIA; TRUNCATE TABLE TB_BACKEND_FILTER; ALTER TABLE TB_ROUTING_CRITERIA ENABLE CONSTRAINT FK_I7GEI6BDR2CDN61HDOPLXBU7P;
ALTER TABLE TB_BACKEND_FILTER DROP COLUMN CRITERIA_OPERATOR;
INSERT INTO TB_BACKEND_FILTER SELECT ID_PK, BACKEND_NAME, PRIORITY FROM TB_BACKEND_FILTER_MIGR;
INSERT INTO TB_ROUTING_CRITERIA SELECT * FROM TB_ROUTING_CRITERIA_MIGR;
